var clipboard = new Clipboard('.item');
clipboard.on('success', function (e) {

    $('#selection').text(e.text);

    chrome.tabs.executeScript(null, {
        code: "var toPaste = '" + e.text + "';"
    }, function () {
        chrome.tabs.executeScript(null, {
            file: "paste.js"
        })
    });
    e.clearSelection();
});
